import {
    Facebook,
    Instagram,
    MailOutline,
    Phone,
    Pinterest,
    Room,
    Twitter,
  } from "@material-ui/icons";
  import styled from "styled-components";


  //For the main footer container
  const Container = styled.div`
    display: flex;

  `;

  //For the left side 
  const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 20px;
  `;


  const Logo = styled.h1``;

  const Desc = styled.p`
    margin: 20px 0px;
  `;

  //For the socialcontainer
  const SocialContainer = styled.div`
    display: flex;
  `;

  //For the SocialIcon
  const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${(props) => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
  `;

  //For the center
  const Center = styled.div`
    flex: 1;
    padding: 20px;

  `;

  //For the title
  const Title = styled.h3`
    margin-bottom: 30px;
  `;

  //For the list
  const List = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
    flex-wrap: wrap;
  `;

  //For the listItem
  const ListItem = styled.li`
    width: 50%;
    margin-bottom: 10px;
  `;

  //For the Right side
  const Right = styled.div`
    flex: 1;
    padding: 20px;

  `;

  //For the ContactItem
  const ContactItem = styled.div`
    margin-bottom: 20px;
    display: flex;
    align-items: center;
  `;

  //For the payment 
  const Payment = styled.img`
      width: 50%;
  `;

  const Footer = () => {
    return (
      <Container>

        <Left>
          <Logo>sweta</Logo>
          <Desc>
          Want to learn more about the site?
          </Desc>
          <SocialContainer>
            <SocialIcon color="3B5999">
              <Facebook />
            </SocialIcon>
            <SocialIcon color="E4405F">
              <Instagram />
            </SocialIcon>
            <SocialIcon color="55ACEE">
              <Twitter />
            </SocialIcon>
            <SocialIcon color="E60023">
              <Pinterest />
            </SocialIcon>
          </SocialContainer>
        </Left>

        <Center>
          <Title>Useful Links</Title>
          <List>
            <ListItem>Home</ListItem>
            <ListItem>Cart</ListItem>
            <ListItem>Man Fashion</ListItem>
            <ListItem>Woman Fashion</ListItem>
            <ListItem>Accessories</ListItem>
            <ListItem>My Account</ListItem>
            <ListItem>Order Tracking</ListItem>
            <ListItem>Wishlist</ListItem>
            <ListItem>Wishlist</ListItem>
            <ListItem>Terms</ListItem>
          </List>
        </Center>

        <Right>
          <Title>Contact</Title>
          <ContactItem>
            <Room style={{marginRight:"10px"}}/> 123 Manchester st, New York 78905
          </ContactItem>
          <ContactItem>
            <Phone style={{marginRight:"10px"}}/> +1 234 56 78
          </ContactItem>
          <ContactItem>
            <MailOutline style={{marginRight:"10px"}} /> contact@sweta.dev
          </ContactItem>
          <Payment src="https://i.ibb.co/Qfvn4z6/payment.png" />
        </Right>
      </Container>
    );
  };

  export default Footer;
