import { Add, Remove } from "@material-ui/icons";
import styled from "styled-components";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";


const Container = styled.div``;

//For wrapper
const Wrapper = styled.div`
  padding: 20px;

`;

//For Title
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

//For Top side
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;

//For TopButton
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;

const TopTexts = styled.div`

`;

//For Top Text
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

//For Buttons
const Bottom = styled.div`
  display: flex;
  justify-content: space-between;

`;

//For informations of products
const Info = styled.div`
  flex: 3;
`;

//For Products
const Product = styled.div`
  display: flex;
  justify-content: space-between;

`;

//For Product Deatail
const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

//For product IMage
const Image = styled.img`
  width: 200px;
`;

//For Product details
const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;


const ProductName = styled.span``;

const ProductId = styled.span``;

//For product color
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;


const ProductSize = styled.span``;

//For product detail
const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

//For product amount container
const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

//For product amount
const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;

`;

//For product price
const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;

`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
`;

//For summary
const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
`;

//For summarytitle
const SummaryTitle = styled.h1`
  font-weight: 200;
`;

//SummaryItem
const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

//For Buttons
const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
`;

const Cart = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />

      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>

            <TopText>Shopping Bag(2)</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>


          <TopButton type="filled">CHECKOUT NOW</TopButton>
        </Top>
        <Bottom>
          <Info>
            <Product>
              <ProductDetail>

                <Image src="https://images.unsplash.com/photo-1562039417-003435a1399e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=722&q=80" />
                <Details>
                  <ProductName>
                    <b>Product:</b> RED DRESS
                  </ProductName>

                  <ProductId>
                    <b>ID:</b> 93813718293
                  </ProductId>

                  <ProductColor color="red" />

                  <ProductSize>
                    <b>Size:</b> small
                  </ProductSize>

                </Details>
              </ProductDetail>
              <PriceDetail>

                <ProductAmountContainer>
                  <Add />
                  <ProductAmount>2</ProductAmount>
                  <Remove />
                </ProductAmountContainer>
                <ProductPrice>$ 50</ProductPrice>

              </PriceDetail>
            </Product>
            <Hr />

            <Product>
              <ProductDetail>
                <Image src="https://images.unsplash.com/photo-1600502281976-e0e31b326509?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2070&q=80g" />
                <Details>

                  <ProductName>
                    <b>Product:</b> leather jacket
                  </ProductName>

                  <ProductId>
                    <b>ID:</b> 93813718293
                  </ProductId>

                  <ProductColor color="gray" />

                  <ProductSize>
                    <b>Size:</b> M
                  </ProductSize>
                </Details>
              </ProductDetail>

              <PriceDetail>
